/**
 * @file Residueindex Colormaker
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { ColormakerRegistry } from "../globals.js";
import Colormaker from "./colormaker.js";


/**
 * Color by residue index
 */
class ResidueindexColormaker extends Colormaker{

    constructor( params ){

        super( params );

        if( !params.scale ){
            this.scale = "rainbow";
        }
        if( !params.domain ){

            // this.domain = [ 0, this.structure.residueStore.count ];

            var scalePerChain = {};

            this.structure.eachChain( function( cp ){
                this.domain = [ cp.residueOffset, cp.residueEnd ];
                scalePerChain[ cp.index ] = this.getScale();
            }.bind( this ) );

            this.atomColor = function( a ){
                return scalePerChain[ a.chainIndex ]( a.residueIndex );
            };

        }else{

            var residueindexScale = this.getScale();

            this.atomColor = function( a ){
                return residueindexScale( a.residueIndex );
            };

        }

    }

}


ColormakerRegistry.add( "residueindex", ResidueindexColormaker );


export default ResidueindexColormaker;

/**
 * @file Selection Colormaker
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { Color } from "../../lib/three.es6.js";

import Selection from "../selection.js";
import Colormaker from "./colormaker.js";
import { ColormakerRegistry } from "../globals.js";


/**
 * Color based on {@link Selection}
 */
class SelectionColormaker extends Colormaker{

    constructor( params ){

        super( params );

        const dataList = params.dataList || [];

        this.colormakerList = [];
        this.selectionList = [];

        dataList.forEach( pair => {

            const [ scheme, sele, params={} ] = pair;

            if( ColormakerRegistry.hasScheme( scheme ) ){
                Object.assign( params, {
                    scheme: scheme,
                    structure: this.structure
                } );
            }else{
                Object.assign( params, {
                    scheme: "uniform",
                    value: new Color( scheme ).getHex()
                } );
            }

            this.colormakerList.push( ColormakerRegistry.getScheme( params ) );
            this.selectionList.push( new Selection( sele ) );

        } );

    }

    atomColor( a ){

        for( let i = 0, n = this.selectionList.length; i < n; ++i ){
            if( this.selectionList[ i ].test( a ) ){
                return this.colormakerList[ i ].atomColor( a );
            }
        }

        return 0xFFFFFF;

    }

}


export default SelectionColormaker;

/**
 * @file Sstruc Colormaker
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { ColormakerRegistry } from "../globals.js";
import Colormaker from "./colormaker.js";

// from Jmol http://jmol.sourceforge.net/jscolors/ (shapely)
var StructureColors = {
    "alphaHelix": 0xFF0080,
    "threeTenHelix": 0xA00080,
    "piHelix": 0x600080,
    "betaStrand": 0xFFC800,
    "betaTurn": 0x6080FF,
    "coil": 0xFFFFFF,

    "dna": 0xAE00FE,
    "rna": 0xFD0162,

    "carbohydrate": 0xA6A6FA
};
var DefaultStructureColor = 0x808080;


/**
 * Color by secondary structure
 */
class SstrucColormaker extends Colormaker{

    constructor( params ){

        super( params );

        this.rp = this.structure.getResidueProxy();

    }

    atomColor( ap ){

        var sstruc = ap.sstruc;
        var rp = this.rp;

        if( sstruc === "h" ){
            return StructureColors.alphaHelix;
        }else if( sstruc === "g" ){
            return StructureColors.threeTenHelix;
        }else if( sstruc === "i" ){
            return StructureColors.piHelix;
        }else if( sstruc === "e" || sstruc === "b" ){
            return StructureColors.betaStrand;
        }else if( sstruc === "t" ){
            return StructureColors.betaTurn;
        }else{
            rp.index = ap.residueIndex;
            if( rp.isDna() ){
                return StructureColors.dna;
            }else if( rp.isRna() ){
                return StructureColors.rna;
            }else if( rp.isSaccharide() ){
                return StructureColors.carbohydrate;
            }else if( rp.isProtein() || sstruc === "s" || sstruc === "l" ){
                return StructureColors.coil;
            }else{
                return DefaultStructureColor;
            }
        }

    }

}


ColormakerRegistry.add( "sstruc", SstrucColormaker );


export default SstrucColormaker;

