 */


import { Vector3 } from "../../lib/three.es6.js";

import { ColormakerRegistry } from "../globals.js";
import Colormaker from "./colormaker.js";


/**
 * Color by volume position
 */
class VolumeColormaker extends Colormaker{

    constructor( params ){

        super( params );

        var volume = this.volume;

        if( volume && volume.inverseMatrix ){

            var valueScale = this.getScale();
            var inverseMatrix = volume.inverseMatrix;
            var data = volume.data;
            var nx = volume.nx;
            var ny = volume.ny;
            var vec = new Vector3();

            this.positionColor = function( coords ){

                vec.copy( coords );
                vec.applyMatrix4( inverseMatrix );
                vec.round();

                var index = ( ( ( ( vec.z * ny ) + vec.y ) * nx ) + vec.x );

                return valueScale( data[ index ] );

            };

        }else{

            var colorValue = this.value;
            this.positionColor = function(){ return colorValue; };

        }

    }

    /**
     * return the color for coordinates in space
     * @param  {Vector3} coords - xyz coordinates
     * @return {Integer} hex coords color
     */
    positionColor( /*coords*/ ){}

}


ColormakerRegistry.add( "volume", VolumeColormaker );


export default VolumeColormaker;

/**
 * @file Collection
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


class Collection{

    constructor( list ){

        this.list = list || [];

        // remove elements from list when they get disposed

        const n = this.list.length;

        for( let i = 0; i < n; ++i ){

            const elm = this.list[ i ];

            elm.signals.disposed.add( this._remove, this );

        }

    }

    _remove( elm ){

        const idx = this.list.indexOf( elm );

        if( idx !== -1 ){

            this.list.splice( idx, 1 );

        }

    }

    _invoke( methodName, methodArgs ){

        const n = this.list.length;

        for( let i = 0; i < n; ++i ){

            const elm = this.list[ i ];
            const method = elm[ methodName ];

            if( typeof method === "function" ){

                method.apply( elm, methodArgs );

            }

        }

        return this;

    }

    setVisibility( value ){

        return this._invoke( "setVisibility", [ value ] );

    }

    setSelection( string ){

        return this._invoke( "setSelection", [ string ] );

    }

    dispose(){

        return this._invoke( "dispose" );

    }

}


export default Collection;

