    run(){

        this.setStatus( "running" );

        this.script.call( this.stage ).then( () => {
            this.setStatus( "finished" );
        } );

        this.setStatus( "called" );

    }

    dispose(){

        // TODO dispose script
        this.signals.disposed.dispatch();

    }

    setVisibility(){}

    getCenter(){}

    getZoom(){}

    getBox(){}

}

ComponentRegistry.add( "script", ScriptComponent );


