        for ( p_B = 0, j = 0; j < mcols; Cp++, p_B++, j++ ) {
            pB = p_B;
            pA = Ap;
            sum = 0.0;
            for ( k = 0; k < ncols; pA++, pB += mcols, k++ ) {
                sum += ad[pA] * bd[pB];
