    var W = new Float64Array( n<<3 );

    for( ; i < n; i++ ) {
        for( k = 0, sd = 0; k < m; k++ ) {
            t = At[i*astep + k];
            sd += t*t;
        }
        W[i] = sd;

        if( Vt ) {
            for( k = 0; k < n; k++ ) {
                Vt[i*vstep + k] = 0;
            }
            Vt[i*vstep + i] = 1;
        }
    }

    for( ; iter < max_iter; iter++ ) {
        changed = 0;

        for( i = 0; i < n-1; i++ ) {
            for( j = i+1; j < n; j++ ) {
                Ai = ( i*astep )|0;
                Aj = ( j*astep )|0;
                a = W[i];
                p = 0;
                b = W[j];

                k = 2;
                p += At[Ai]*At[Aj];
                p += At[Ai+1]*At[Aj+1];

                for( ; k < m; k++ )
                    p += At[Ai+k]*At[Aj+k];

                if( Math.abs( p ) <= eps*Math.sqrt( a*b ) ) continue;

                p *= 2.0;
                beta = a - b;
                gamma = hypot( p, beta );
                if( beta < 0 ) {
                    delta = ( gamma - beta )*0.5;
                    s = Math.sqrt( delta/gamma );
                    c = ( p/( gamma*s*2.0 ) );
                } else {
                    c = Math.sqrt( ( gamma + beta )/( gamma*2.0 ) );
                    s = ( p/( gamma*c*2.0 ) );
                }

                a=0.0;
                b=0.0;

                k = 2; // unroll
                t0 = c*At[Ai] + s*At[Aj];
                t1 = -s*At[Ai] + c*At[Aj];
                At[Ai] = t0; At[Aj] = t1;
                a += t0*t0; b += t1*t1;

                t0 = c*At[Ai+1] + s*At[Aj+1];
                t1 = -s*At[Ai+1] + c*At[Aj+1];
                At[Ai+1] = t0; At[Aj+1] = t1;
                a += t0*t0; b += t1*t1;

                for( ; k < m; k++ )
                {
                    t0 = c*At[Ai+k] + s*At[Aj+k];
                    t1 = -s*At[Ai+k] + c*At[Aj+k];
                    At[Ai+k] = t0; At[Aj+k] = t1;

                    a += t0*t0; b += t1*t1;
                }

                W[i] = a;
                W[j] = b;

                changed = 1;

                if( Vt ) {
                    Vi = ( i*vstep )|0;
                    Vj = ( j*vstep )|0;

                    k = 2;
                    t0 = c*Vt[Vi] + s*Vt[Vj];
                    t1 = -s*Vt[Vi] + c*Vt[Vj];
                    Vt[Vi] = t0; Vt[Vj] = t1;

                    t0 = c*Vt[Vi+1] + s*Vt[Vj+1];
                    t1 = -s*Vt[Vi+1] + c*Vt[Vj+1];
                    Vt[Vi+1] = t0; Vt[Vj+1] = t1;

                    for( ; k < n; k++ ) {
                        t0 = c*Vt[Vi+k] + s*Vt[Vj+k];
                        t1 = -s*Vt[Vi+k] + c*Vt[Vj+k];
                        Vt[Vi+k] = t0; Vt[Vj+k] = t1;
