            for( iter = 0; iter < 2; iter++ ) {
                for( j = 0; j < i; j++ ) {
                    sd = 0;
                    for( k = 0; k < m; k++ ) {
                        sd += At[i*astep + k]*At[j*astep + k];
                    }
                    asum = 0.0;
                    for( k = 0; k < m; k++ ) {
                        t = ( At[i*astep + k] - sd*At[j*astep + k] );
                        At[i*astep + k] = t;
                        asum += Math.abs( t );
                    }
                    asum = asum ? 1.0/asum : 0;
                    for( k = 0; k < m; k++ ) {
                        At[i*astep + k] *= asum;
                    }
                }
            }
            sd = 0;
            for( k = 0; k < m; k++ ) {
                t = At[i*astep + k];
                sd += t*t;
            }
            sd = Math.sqrt( sd );
        }

        s = ( 1.0/sd );
        for( k = 0; k < m; k++ ) {
            At[i*astep + k] *= s;
        }
    }
}


function svd( A, W, U, V ){
