    if( at === 0 ) {
        transpose( a_mt, A );
    } else {
        for( i = 0; i < _n*_m; i++ ) {
            a_mt.data[i] = A.data[i];
        }
        for( ; i < n*m; i++ ) {
            a_mt.data[i] = 0;
        }
    }

    JacobiSVDImpl( a_mt.data, m, w_mt.data, v_mt.data, n, m, n, m );

    if( W ) {
        for( i=0; i < n; i++ ) {
            W.data[i] = w_mt.data[i];
        }
        for( ; i < _n; i++ ) {
            W.data[i] = 0;
        }
    }

    if ( at === 0 ) {
        if( U ) transpose( U, a_mt );
        if( V ) transpose( V, v_mt );
    } else {
        if( U ) transpose( U, v_mt );
        if( V ) transpose( V, a_mt );
    }
}

