function applyMatrix4toVector3array( m, a ){
    for( var i = 0, il = a.length; i < il; i+=3 ){
        var x = a[ i ], y = a[ i + 1 ], z = a[ i + 2 ];
        a[ i     ] = m[ 0 ] * x + m[ 4 ] * y + m[ 8 ]  * z + m[ 12 ];
        a[ i + 1 ] = m[ 1 ] * x + m[ 5 ] * y + m[ 9 ]  * z + m[ 13 ];
        a[ i + 2 ] = m[ 2 ] * x + m[ 6 ] * y + m[ 10 ] * z + m[ 14 ];
