function applyMatrix3toVector3array( m, a ){
    for( var i = 0, il = a.length; i < il; i+=3 ){
        var x = a[ i ], y = a[ i + 1 ], z = a[ i + 2 ];
        a[ i     ] = m[ 0 ] * x + m[ 3 ] * y + m[ 6 ] * z;
        a[ i + 1 ] = m[ 1 ] * x + m[ 4 ] * y + m[ 7 ] * z;
        a[ i + 2 ] = m[ 2 ] * x + m[ 5 ] * y + m[ 8 ] * z;
