import Parser from "./parser.js";


class XmlParser extends Parser{

    constructor( streamer, params ){

        const p = params || {};

        super( streamer, p );

        this.useDomParser = defaults( p.useDomParser, false );

        this.xml = {
            name: this.name,
            path: this.path,
            data: {}
        };

    }

    get type (){ return "xml"; }
    get __objName (){ return "xml"; }

    __xmlParser( xml ){

        return parseXml( xml );

    }

