        const domParser = new window.DOMParser();
        return domParser.parseFromString( xml, "text/xml" );

    }

    _parse(){

        if( Debug ) Log.time( "XmlParser._parse " + this.name );

        if( this.useDomParser ){
            if( this.streamer.data instanceof Document ){
