    finalizeBonds(){

        this.bondSet = this.getBondSet();
        this.bondCount = this.bondStore.count;
        this.bondHash = new BondHash( this.bondStore, this.atomStore.count );

        this.atomSetCache = {};
        if( !this.atomSetDict.rung ){
