class Trajectory{

    constructor( trajPath, structure, params ){

        this.signals = {
            gotNumframes: new Signal(),
            frameChanged: new Signal(),
            selectionChanged: new Signal(),
            playerChanged: new Signal(),
        };

        var p = params || {};
