                );
                coords[ j + 1 ] = spline(
                    cppp[ j + 1 ], cpp[ j + 1 ], cp[ j + 1 ], c[ j + 1 ], t, 1
                );
                coords[ j + 2 ] = spline(
                    cppp[ j + 2 ], cpp[ j + 2 ], cp[ j + 2 ], c[ j + 2 ], t, 1
                );

            }

        }else{

            for( j = 0; j < m; j += 3 ){

                coords[ j + 0 ] = lerp( cp[ j + 0 ], c[ j + 0 ], t );
