/**
 * @file Label Factory
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { AA1 } from "../structure/structure-constants.js";


function LabelFactory( type, text ){

    this.type = type;
    this.text = text || {};

}

LabelFactory.prototype = {

    constructor: LabelFactory,

    atomLabel: function( a ){

        var type = this.type;

        var l;

        switch( type ){

            case "atomname":
                l = a.atomname;
                break;

            case "atomindex":
                l = "" + a.index;
                break;

            case "occupancy":
                l = a.occupancy.toFixed( 2 );
                break;

            case "bfactor":
                l = a.bfactor.toFixed( 2 );
                break;

            case "serial":
                l = "" + a.serial;
                break;

            case "element":
                l = a.element;
                break;

            case "atom":
                l = a.atomname + "|" + a.index;
                break;

            case "resname":
                l = a.resname;
                break;

            case "resno":
                l = "" + a.resno;
                break;

            case "res":
                var resname = a.resname.toUpperCase();
                l = ( AA1[ resname ] || resname ) + a.resno;
                break;

            case "text":
                l = this.text[ a.index ];
                break;

            // case "qualified":
            default:
                l = a.qualifiedName();
                break;

        }

        return l === undefined ? '' : l;

    }

};

LabelFactory.types = {

    "": "",
    "atomname": "atom name",
    "atomindex": "atom index",
    "occupancy": "occupancy",
    "bfactor": "b-factor",
    "serial": "serial",
    "element": "element",
    "atom": "atom name + index",
    "resname": "residue name",
    "resno": "residue no",
    "res": "residue name + no",
    "text": "text",
    "qualified": "qualified name"

};


export default LabelFactory;

/**
 * @file Parse Xml
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


// https://github.com/segmentio/xml-parser
// MIT license


const reStrip = /^['"]|['"]$/g;
const reTag = /^<([\w-:.]+)\s*/;
const reContent = /^([^<]*)/;
const reAttr = /([\w:-]+)\s*=\s*("[^"]*"|'[^']*'|\w+)\s*/;


function strip( val ){
    return val.replace( reStrip, '' );
}


function parseXml( xml ){

    // trim and strip comments
    xml = xml.trim().replace( /<!--[\s\S]*?-->/g, '' );

    return document();

    function document(){
        return {
            declaration: declaration(),
            root: tag()
        };
    }

    function declaration(){
        const m = match( /^<\?xml\s*/ );
        if ( !m ) return;
        // tag
        const node = {
            attributes: {}
        };
        // attributes
        while ( !( eos() || is( '?>' ) ) ) {
            const attr = attribute();
            if ( !attr ) return node;
            node.attributes[attr.name] = attr.value;
        }
        match( /\?>\s*/ );
        return node;
    }

    function tag(){
        const m = match( reTag );
        if ( !m ) return;
        // name
        const node = {
            name: m[1],
            attributes: {},
            children: []
        };
        // attributes
        while ( !( eos() || is( '>' ) || is( '?>' ) || is( '/>' ) ) ) {
            var attr = attribute();
            if ( !attr ) return node;
            node.attributes[attr.name] = attr.value;
        }
        // self closing tag
        if ( match( /^\s*\/>\s*/ ) ) {
            return node;
        }
        match( /\??>\s*/ );
        // content
        node.content = content();
        // children
        let child;
        while ( ( child = tag() ) ) {
            node.children.push( child );
        }
        // closing
        match( /^<\/[\w-:.]+>\s*/ );
        return node;
    }

    function content(){
        const m = match( reContent );
        if ( m ) return m[1];
        return '';
    }

    function attribute(){
        const m = match( reAttr );
        if ( !m ) return;
        return { name: m[1], value: strip( m[2] ) };
    }

    function match( re ){
        const m = xml.match( re );
        if ( !m ) return;
        xml = xml.slice( m[0].length );
        return m;
    }

    function eos(){
        return 0 === xml.length;
    }

    function is( prefix ){
        return 0 === xml.indexOf( prefix );
    }

}


export {
    parseXml
};

/**
 * @file Picker
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { Color, Vector3 } from "../../lib/three.es6.js";

import { calculateMeanVector3 } from "../math/vector-utils.js";
import Selection from "../selection.js";


/**
 * Picker class
 * @interface
 */
class Picker{

    /**
     * @param  {Array|TypedArray} [array] - mapping
     */
    constructor( array ){
        this.array = array;
    }

    /**
     * Get the index for the given picking id
     * @param  {Integer} pid - the picking id
     * @return {Integer} the index
     */
    getIndex( pid ){
        return this.array ? this.array[ pid ] : pid;
    }

    /**
     * Get object data
     * @abstract
     * @param  {Integer} pid - the picking id
     * @return {Object} the object data
     */
    getObject( /*pid*/ ){
        return {};
    }

    _applyTransformations( vector, instance, component ){
        if( instance ){
            vector.applyMatrix4( instance.matrix );
        }
        if( component ){
            vector.applyMatrix4( component.matrix );
        }
        return vector;
    }

    /**
     * Get object position
     * @abstract
     * @param  {Integer} pid - the picking id
     * @return {Vector3} the object position
     */
    _getPosition( /*pid*/ ){
        return new Vector3();
    }

    /**
     * Get position for the given picking id
     * @param  {Integer} pid - the picking id
     * @param  {Object} instance - the instance that should be applied
     * @param  {Component} component - the component of the picked object
     * @return {Vector3} the position
     */
    getPosition( pid, instance, component ){
        return this._applyTransformations(
            this._getPosition( pid ), instance, component
        );
    }

}


/**
 * Shape picker class
 * @interface
 */
class ShapePicker extends Picker{

    /**
     * @param  {Shape} shape - shape object
     */
    constructor( shape ){
        super();
        this.shape = shape;
    }

    get data (){ return this.shape; }

}


//


class CylinderPicker extends ShapePicker{

    get type (){ return "cylinder"; }

    getObject( pid ){
        const s = this.shape;
        return {
            shape: s,
            color: new Color().fromArray( s.cylinderColor, 3 * pid ),
            radius: s.cylinderRadius[ pid ],
            position1: new Vector3().fromArray( s.cylinderPosition1, 3 * pid ),
            position2: new Vector3().fromArray( s.cylinderPosition2, 3 * pid ),
            name: s.cylinderName[ pid ]
        };
    }

    _getPosition( pid ){
        const s = this.shape;
        const p1 = new Vector3().fromArray( s.cylinderPosition1, 3 * pid );
        const p2 = new Vector3().fromArray( s.cylinderPosition2, 3 * pid );
        return p1.add( p2 ).multiplyScalar( 0.5 );
    }

}


class ArrowPicker extends ShapePicker{

    get type (){ return "arrow"; }

    getObject( pid ){
        const s = this.shape;
        return {
            shape: s,
            position: this._getPosition( pid ),
            position1: new Vector3().fromArray( s.arrowPosition1, 3 * pid ),
            position2: new Vector3().fromArray( s.arrowPosition2, 3 * pid ),
            color: new Color().fromArray( s.arrowColor, 3 * pid ),
            radius: s.arrowRadius[ pid ],
            name: s.arrowName[ pid ]
        };
    }

    _getPosition( pid ){
        const s = this.shape;
        const p1 = new Vector3().fromArray( s.arrowPosition1, 3 * pid );
        const p2 = new Vector3().fromArray( s.arrowPosition2, 3 * pid );
        return p1.add( p2 ).multiplyScalar( 0.5 );
    }

}


class AtomPicker extends Picker{

    constructor( array, structure ){
        super( array );
        this.structure = structure;
    }

    get type (){ return "atom"; }
    get data (){ return this.structure; }

    getObject( pid ){
        return this.structure.getAtomProxy( this.getIndex( pid ) );
    }

    _getPosition( pid ){
        return new Vector3().copy( this.getObject( pid ) );
    }

}


class AxesPicker extends Picker{

    constructor( axes ){
        super();
        this.axes = axes;
    }

    get type (){ return "axes"; }
    get data (){ return this.axes; }

    getObject( /*pid*/ ){
        return {
            axes: this.axes
        };
    }

    _getPosition( /*pid*/ ){
        return this.axes.center.clone();
    }

}


class BondPicker extends Picker{

    constructor( array, structure, bondStore ){
        super( array );
        this.structure = structure;
        this.bondStore = bondStore || structure.bondStore;
    }

    get type (){ return "bond"; }
    get data (){ return this.structure; }

    getObject( pid ){
        var bp = this.structure.getBondProxy( this.getIndex( pid ) );
        bp.bondStore = this.bondStore;
        return bp;
    }

    _getPosition( pid ){
        const b = this.getObject( pid );
        return new Vector3()
            .copy( b.atom1 )
            .add( b.atom2 )
            .multiplyScalar( 0.5 );
    }

}


class ContactPicker extends BondPicker{

    get type (){ return "contact"; }

}


class ConePicker extends ShapePicker{

    get type (){ return "cone"; }

    getObject( pid ){
        const s = this.shape;
        return {
            shape: s,
            position: this._getPosition( pid ),
            position1: new Vector3().fromArray( s.conePosition1, 3 * pid ),
            position2: new Vector3().fromArray( s.conePosition2, 3 * pid ),
            color: new Color().fromArray( s.coneColor, 3 * pid ),
            radius: s.coneRadius[ pid ],
            name: s.coneName[ pid ]
        };
    }

    _getPosition( pid ){
        const s = this.shape;
        const p1 = new Vector3().fromArray( s.conePosition1, 3 * pid );
        const p2 = new Vector3().fromArray( s.conePosition2, 3 * pid );
        return p1.add( p2 ).multiplyScalar( 0.5 );
    }

}


class ClashPicker extends Picker{

    constructor( array, validation, structure ){
        super( array );
        this.validation = validation;
        this.structure = structure;
    }

    get type (){ return "clash"; }
    get data (){ return this.validation; }

    getObject( pid ){
        const val = this.validation;
        const idx = this.getIndex( pid );
        return {
            validation: val,
            index: idx,
            clash: val.clashArray[ idx ]
        };
    }

    _getAtomProxyFromSele( sele ){
        const selection = new Selection( sele );
        const idx = this.structure.getAtomIndices( selection )[ 0 ];
        return this.structure.getAtomProxy( idx );
    }

    _getPosition( pid ){
        const clash = this.getObject( pid ).clash;
        const ap1 = this._getAtomProxyFromSele( clash.sele1 );
        const ap2 = this._getAtomProxyFromSele( clash.sele2 );
        return new Vector3().copy( ap1 ).add( ap2 ).multiplyScalar( 0.5 );
    }

}


class DistancePicker extends BondPicker{

    get type (){ return "distance"; }

}


class EllipsoidPicker extends ShapePicker{

    get type (){ return "ellipsoid"; }

    getObject( pid ){
        const s = this.shape;
        return {
            shape: s,
            position: this._getPosition( pid ),
            color: new Color().fromArray( s.ellipsoidColor, 3 * pid ),
            radius: s.ellipsoidRadius[ pid ],
            majorAxis: new Vector3().fromArray( s.ellipsoidMajorAxis, 3 * pid ),
            minorAxis: new Vector3().fromArray( s.ellipsoidMinorAxis, 3 * pid ),
            name: s.ellipsoidName[ pid ]
        };
    }

    _getPosition( pid ){
        return new Vector3().fromArray( this.shape.ellipsoidPosition, 3 * pid );
    }

}


class IgnorePicker extends Picker{

    get type (){ return "ignore"; }

}


class MeshPicker extends ShapePicker{

    constructor( shape, mesh ){
        super( shape );
        this.mesh = mesh;
    }

    get type (){ return "mesh"; }

    getObject( /*pid*/ ){
        const m = this.mesh;
        return {
            shape: this.shape,
            name: m.name,
            serial: m.serial
        };
    }

    _getPosition( /*pid*/ ){
        if( !this.__position ){
            this.__position = calculateMeanVector3( this.mesh.position );
        }
        return this.__position;
    }

}


class SpherePicker extends ShapePicker{

    get type (){ return "sphere"; }

    getObject( pid ){
        const s = this.shape;
        return {
            shape: s,
            position: this._getPosition( pid ),
            color: new Color().fromArray( s.sphereColor, 3 * pid ),
            radius: s.sphereRadius[ pid ],
            name: s.sphereName[ pid ]
        };
    }

    _getPosition( pid ){
        return new Vector3().fromArray( this.shape.spherePosition, 3 * pid );
    }

}


class SurfacePicker extends Picker{

    constructor( array, surface ){
        super( array );
        this.surface = surface;
    }

    get type (){ return "surface"; }
    get data (){ return this.surface; }

    getObject( pid ){
        return {
            surface: this.surface,
            index: this.getIndex( pid )
        };
    }

    _getPosition( /*pid*/ ){
        return this.surface.center.clone();
    }

}


class UnitcellPicker extends Picker{

    constructor( unitcell, structure ){
        super();
        this.unitcell = unitcell;
        this.structure = structure;
    }

    get type (){ return "unitcell"; }
    get data (){ return this.unitcell; }

    getObject( /*pid*/ ){
        return {
            unitcell: this.unitcell,
            structure: this.structure
        };
    }

    _getPosition( /*pid*/ ){
        return this.unitcell.getCenter( this.structure );
    }

}


class UnknownPicker extends Picker{

    get type (){ return "unknown"; }

}


class VolumePicker extends Picker{

    constructor( array, volume ){
        super( array );
        this.volume = volume;
    }

    get type (){ return "volume"; }
    get data (){ return this.volume; }

    getObject( pid ){
        const vol = this.volume;
        const idx = this.getIndex( pid );
        return {
            volume: vol,
            index: idx,
            value: vol.data[ idx ]
        };
    }

    _getPosition( pid ){
        const dp = this.volume.position;
        const idx = this.getIndex( pid );
        return new Vector3(
            dp[ idx * 3 ],
            dp[ idx * 3 + 1 ],
            dp[ idx * 3 + 2 ]
        );
    }

}


class SlicePicker extends VolumePicker{

    get type (){ return "slice"; }

}


export {
    Picker,
    ShapePicker,
    ArrowPicker,
    AtomPicker,
    AxesPicker,
    BondPicker,
    ConePicker,
    ContactPicker,
    CylinderPicker,
    ClashPicker,
    DistancePicker,
    EllipsoidPicker,
    IgnorePicker,
    MeshPicker,
    SlicePicker,
    SpherePicker,
    SurfacePicker,
    UnitcellPicker,
    UnknownPicker,
    VolumePicker
};

/**
 * @file Queue
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


function Queue( fn, argList ){

    var queue = [];
    var pending = false;

    if( argList ){
        for( var i = 0, il = argList.length; i < il; ++i ){
            queue.push( argList[ i ] );
        }
        next();
    }

    function run( arg ){
        fn( arg, next );
    }

    function next(){
        var arg = queue.shift();
        if( arg !== undefined ){
            pending = true;
            setTimeout( function(){ run( arg ); } );
        }else{
            pending = false;
        }
    }

    // API

    this.push = function( arg ){
        queue.push( arg );
        if( !pending ) next();
    };

    this.kill = function(){
        queue.length = 0;
    };

    this.length = function(){
        return queue.length;
    };

}


export default Queue;

/**
 * @file Radius Factory
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import {
    VdwRadii, DefaultVdwRadius, CovalentRadii, DefaultCovalentRadius,
    NucleicBackboneAtoms
} from "../structure/structure-constants";


function RadiusFactory( type, scale ){

    this.type = type;
    this.scale = scale || 1.0;

    this.max = 10;

}

RadiusFactory.prototype = {

    constructor: RadiusFactory,

    atomRadius: function( a ){

        var type = this.type;
        var scale = this.scale;

        var r;

        switch( type ){

            case "vdw":

                r = VdwRadii[ a.element ] || DefaultVdwRadius;
                break;

            case "covalent":

                r = CovalentRadii[ a.element ] || DefaultCovalentRadius;
                break;

            case "bfactor":

                r = a.bfactor || 1.0;
                break;

            case "sstruc":

                var sstruc = a.sstruc;
                if( sstruc === "h" ){
                    r = 0.25;
                }else if( sstruc === "g" ){
                    r = 0.25;
                }else if( sstruc === "i" ){
                    r = 0.25;
                }else if( sstruc === "e" ){
                    r = 0.25;
                }else if( sstruc === "b" ){
                    r = 0.25;
                }else if( NucleicBackboneAtoms.includes( a.atomname ) ){
                    r = 0.4;
                }else{
                    r = 0.1;
                }
                break;

            default:

                r = type || 1.0;
                break;

        }

        return Math.min( r * scale, this.max );

    }

};

RadiusFactory.types = {

    "": "",
    "vdw": "by vdW radius",
    "covalent": "by covalent radius",
    "sstruc": "by secondary structure",
    "bfactor": "by bfactor",
    "size": "size"

};


export default RadiusFactory;

/**
 * @file Registry
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { defaults } from "../utils.js";


function toLowerCaseString( value ){
    return defaults( value, "" ).toString().toLowerCase();
}


class Registry{

    constructor( name ){
        this.name = name;
        this._dict = {};
    }

    add( key, value ){
        this._dict[ toLowerCaseString( key ) ] = value;
    }

    get( key ){
        return this._dict[ toLowerCaseString( key ) ];
    }

    get names(){
        return Object.keys( this._dict );
    }

}


export default Registry;

/**
 * @file Utils
 * @author Alexander Rose <alexander.rose@weirdbyte.de>
 * @private
 */


import { Vector2, Vector3, Matrix4, Quaternion } from "../lib/three.es6.js";


function getQuery( id ){

    if( typeof window === "undefined" ) return undefined;

    var a = new RegExp( id + "=([^&#=]*)" );
    var m = a.exec( window.location.search );

    if( m ){
        return decodeURIComponent( m[1] );
    }else{
        return undefined;
    }

}


function boolean( value ){

    if( !value ){
        return false;
    }

    if( typeof value === "string" ){
        return /^1|true|t|yes|y$/i.test( value );
    }

    return true;

}


function defaults( value, defaultValue ){

    return value !== undefined ? value : defaultValue;

}


function pick( object ){

    var properties = [].slice.call( arguments, 1 );

    return properties.reduce( ( a, e ) => ( a[ e ] = object[ e ], a ), {} );

}


function getProtocol(){

    var protocol = window.location.protocol;
    return protocol.match( /http(s)?:/gi ) === null ? "http:" : protocol;

}


function getBrowser(){

    if( typeof window === "undefined" ) return false;

    var ua = window.navigator.userAgent;

    if ( /Opera|OPR/.test( ua ) ) {

        return 'Opera';

    } else if ( /Chrome/i.test( ua ) ) {

        return 'Chrome';

    } else if ( /Firefox/i.test( ua ) ) {

        return 'Firefox';

    } else if ( /Mobile(\/.*)? Safari/i.test( ua ) ) {

        return 'Mobile Safari';

    } else if ( /MSIE/i.test( ua ) ) {

        return 'Internet Explorer';

    } else if ( /Safari/i.test( ua ) ) {

        return 'Safari';

    }

    return false;

}


function getAbsolutePath( relativePath ){

    var loc = window.location;
    var pn = loc.pathname;
    var basePath = pn.substring( 0, pn.lastIndexOf( "/" ) + 1 );

    return loc.origin + basePath + relativePath;

}


function deepCopy( src ){

    if( typeof src !== "object" ){
        return src;
    }

    var dst = Array.isArray( src ) ? [] : {};

    for( var key in src ){
        dst[ key ] = deepCopy( src[ key ] );
    }

    return dst;

}


function download( data, downloadName ){

    // using ideas from https://github.com/eligrey/FileSaver.js/blob/master/FileSaver.js

    if( !data ) return;

    downloadName = downloadName || "download";

    var isSafari = getBrowser() === "Safari";
    var isChromeIos = /CriOS\/[\d]+/.test( window.navigator.userAgent );

    var a = document.createElement( 'a' );

    function openUrl( url ){
        var opened = window.open( url, '_blank' );
        if( !opened ){
            window.location.href = url;
        }
    }

    function open( str ){
        openUrl( isChromeIos ? str : str.replace( /^data:[^;]*;/, 'data:attachment/file;' ) );
    }

    if( typeof navigator !== "undefined" && navigator.msSaveOrOpenBlob ){

        // native saveAs in IE 10+
        navigator.msSaveOrOpenBlob( data, downloadName );

    }else if( ( isSafari || isChromeIos ) && window.FileReader ){

