            open( data );
        }

    }else{

        if( data instanceof Blob ){
            data = URL.createObjectURL( data );
        }

        if( "download" in a ){
            // download link available
            a.style.display = "hidden";
            document.body.appendChild( a );
            a.href = data;
            a.download = downloadName;
            a.target = "_blank";
