            fogColor: new Color( 0x000000 ),
            fogNear: 50,
            fogFar: 100,

            backgroundColor: new Color( 0x000000 ),

            cameraType: "perspective",
            cameraFov: 40,
            cameraZ: -80, // FIXME initial value should be automatically determined

            clipNear: 0,
            clipFar: 100,
            clipDist: 10,

